$(document).ready(function () {
    $('#example').DataTable({
        processing: true,
        serverSide: true,
        ajax: {
            url: "{{ url_for('Data_array') }}",
            "type": "POST"
        },

        columns: [
              { data: 'upc' },
              { data: 'salePrice' },
              { data: 'name' },
              { data: 'brandName' },
              { data: 'modelNumber' },
              { data: 'largeImage' },
              { data: 'quantity' },
              { data: 'freeShippingOver50Dollars' },
              { data: 'IN_stock' },
              { data: 'Update_time' },

        ],
    });
})